import { Todo, CreateTodoInput, UpdateTodoInput } from './todo';

/**
 * Action types for todo state management
 */
export enum TodoActionType {
  ADD_TODO = 'ADD_TODO',
  UPDATE_TODO = 'UPDATE_TODO',
  DELETE_TODO = 'DELETE_TODO',
  TOGGLE_TODO = 'TOGGLE_TODO',
  CLEAR_COMPLETED = 'CLEAR_COMPLETED',
  SET_FILTER = 'SET_FILTER',
  LOAD_TODOS = 'LOAD_TODOS'
}

/**
 * Individual action interfaces
 */
export interface AddTodoAction {
  type: TodoActionType.ADD_TODO;
  payload: CreateTodoInput;
}

export interface UpdateTodoAction {
  type: TodoActionType.UPDATE_TODO;
  payload: {
    id: string;
    updates: UpdateTodoInput;
  };
}

export interface DeleteTodoAction {
  type: TodoActionType.DELETE_TODO;
  payload: {
    id: string;
  };
}

export interface ToggleTodoAction {
  type: TodoActionType.TOGGLE_TODO;
  payload: {
    id: string;
  };
}

export interface ClearCompletedAction {
  type: TodoActionType.CLEAR_COMPLETED;
}

export interface SetFilterAction {
  type: TodoActionType.SET_FILTER;
  payload: {
    filter: import('./filters').TodoFilter;
  };
}

export interface LoadTodosAction {
  type: TodoActionType.LOAD_TODOS;
  payload: {
    todos: Todo[];
  };
}

/**
 * Union type of all possible todo actions
 */
export type TodoAction =
  | AddTodoAction
  | UpdateTodoAction
  | DeleteTodoAction
  | ToggleTodoAction
  | ClearCompletedAction
  | SetFilterAction
  | LoadTodosAction;

