import { Todo } from './todo';
import { TodoFilter } from './filters';

/**
 * Utility type for making certain properties required
 */
export type RequiredFields<T, K extends keyof T> = T & Required<Pick<T, K>>;

/**
 * Utility type for optional fields except specified ones
 */
export type OptionalExcept<T, K extends keyof T> = Partial<T> & Pick<T, K>;

/**
 * Generic API response wrapper
 */
export interface ApiResponse<T> {
  data: T;
  success: boolean;
  message?: string;
  timestamp: Date;
}

/**
 * Error type for todo operations
 */
export interface TodoError {
  code: string;
  message: string;
  field?: keyof Todo;
}

/**
 * Sorting options for todos
 */
export type TodoSortBy = 'createdAt' | 'updatedAt' | 'text' | 'completed';
export type SortOrder = 'asc' | 'desc';

export interface TodoSortConfig {
  sortBy: TodoSortBy;
  order: SortOrder;
}

/**
 * Function type for filtering todos
 */
export type TodoFilterFunction = (todo: Todo) => boolean;

/**
 * Predefined filter functions
 */
export const filterFunctions: Record<TodoFilter, TodoFilterFunction> = {
  all: () => true,
  active: (todo) => !todo.completed,
  completed: (todo) => todo.completed
};

/**
 * Type guard to check if a value is a valid TodoFilter
 */
export function isTodoFilter(value: string): value is TodoFilter {
  return ['all', 'active', 'completed'].includes(value);
}
EOF

