/**
 * Test utilities and examples for useTodos hook
 * This file demonstrates proper usage and error handling scenarios
 */

import { renderHook, act } from '@testing-library/react';
import { useTodos } from './useTodos';

// Mock localStorage for testing
const localStorageMock = (() => {
  let store: Record<string, string> = {};

  return {
    getItem: (key: string) => store[key] || null,
    setItem: (key: string, value: string) => {
      store[key] = value.toString();
    },
    removeItem: (key: string) => {
      delete store[key];
    },
    clear: () => {
      store = {};
    },
  };
})();

Object.defineProperty(window, 'localStorage', {
  value: localStorageMock,
});

// Mock crypto.randomUUID for consistent testing
Object.defineProperty(global, 'crypto', {
  value: {
    randomUUID: () => 'mock-uuid-' + Math.random().toString(36).substr(2, 9),
  },
});

/**
 * Example usage scenarios for the useTodos hook
 */
export const useTodosExamples = {
  /**
   * Basic usage example
   */
  basicUsage: () => {
    const {
      todos,
      filteredTodos,
      addTodo,
      toggleTodo,
      deleteTodo,
      setFilter,
      activeTodosCount,
      completedTodosCount,
      clearCompleted,
      isLoading,
      error
    } = useTodos();

    // Example operations:
    // addTodo({ text: "Learn React" });
    // toggleTodo(todoId);
    // deleteTodo(todoId);
    // setFilter('active');
    // clearCompleted();

    return {
      todos,
      filteredTodos,
      activeTodosCount,
      completedTodosCount,
      isLoading,
      error,
      actions: {
        addTodo,
        toggleTodo,
        deleteTodo,
        setFilter,
        clearCompleted
      }
    };
  },

  /**
   * Error handling scenarios
   */
  errorHandling: {
    emptyTodoText: () => {
      const { addTodo, error } = useTodos();

      // This should trigger an error
      act(() => {
        addTodo({ text: '' });
      });

      return { error }; // Should contain "Todo text cannot be empty"
    },

    localStorageFailure: () => {
      // Simulate localStorage failure
      const originalSetItem = localStorage.setItem;
      localStorage.setItem = () => {
        throw new Error('Storage quota exceeded');
      };

      const { addTodo, error } = useTodos();

      act(() => {
        addTodo({ text: 'Test todo' });
      });

      // Restore original implementation
      localStorage.setItem = originalSetItem;

      return { error }; // Should handle gracefully
    }
  },

  /**
   * Filtering examples
   */
  filtering: () => {
    const { setFilter, currentFilter, filteredTodos } = useTodos();

    const examples = {
      showAll: () => act(() => setFilter('all')),
      showActive: () => act(() => setFilter('active')),
      showCompleted: () => act(() => setFilter('completed'))
    };

    return {
      currentFilter,
      filteredTodos,
      examples
    };
  },

  /**
   * Persistence example
   */
  persistence: () => {
    // First hook instance
    const { result: result1 } = renderHook(() => useTodos());

    act(() => {
      result1.current.addTodo({ text: 'Persistent todo' });
    });

    // Second hook instance should load the same data
    const { result: result2 } = renderHook(() => useTodos());

    return {
      firstInstance: result1.current.todos,
      secondInstance: result2.current.todos
    };
  }
};

/**
 * Type-safe usage patterns
 */
export const typeSafeUsage = {
  /**
   * Component integration example
   */
  withTypeScript: () => {
    const todoHook = useTodos();

    // All properties are properly typed
    const {
      todos,              // Todo[]
      filteredTodos,      // Todo[]
      currentFilter,      // TodoFilter
      isLoading,          // boolean
      error,              // string | null
      activeTodosCount,   // number
      completedTodosCount,// number
      allTodosCount,      // number
      hasCompletedTodos,  // boolean
      addTodo,            // (input: CreateTodoInput) => void
      updateTodo,         // (id: string, updates: UpdateTodoInput) => void
      deleteTodo,         // (id: string) => void
      toggleTodo,         // (id: string) => void
      clearCompleted,     // () => void
      setFilter,          // (filter: TodoFilter) => void
      clearError          // () => void
    } = todoHook;

    return todoHook;
  }
};

export default useTodos;

