 */
export interface Todo {
  id: string;
  text: string;
  completed: boolean;
  createdAt: Date;
  updatedAt: Date;
}

/**
 * Input type for creating a new todo (omits auto-generated fields)
 */
export type CreateTodoInput = Pick<Todo, 'text'>;

/**
 * Input type for updating an existing todo
 */
export type UpdateTodoInput = Partial<Pick<Todo, 'text' | 'completed'>>;

/**
 * Todo with optional fields for partial updates
 */
export type PartialTodo = Partial<Todo> & Pick<Todo, 'id'>;

