/**
 * Example TodoApp component demonstrating useTodos hook usage
 */
import React, { useState } from 'react';
import { useTodos } from '../hooks/useTodos';

export const TodoApp: React.FC = () => {
  const {
    filteredTodos,
    currentFilter,
    isLoading,
    error,
    activeTodosCount,
    completedTodosCount,
    hasCompletedTodos,
    addTodo,
    toggleTodo,
    deleteTodo,
    setFilter,
    clearCompleted,
    clearError
  } = useTodos();

  const [inputValue, setInputValue] = useState('');

  const handleAddTodo = (e: React.FormEvent) => {
    e.preventDefault();
    if (inputValue.trim()) {
      addTodo({ text: inputValue.trim() });
      setInputValue('');
    }
  };

  const handleFilterChange = (filter: 'all' | 'active' | 'completed') => {
    setFilter(filter);
  };

  if (isLoading) {
    return <div className="loading">Loading todos...</div>;
  }

  return (
    <div className="todo-app">
      <header>
        <h1>Todo App</h1>

        {error && (
          <div className="error">
            {error}
            <button onClick={clearError}>×</button>
          </div>
        )}

        <form onSubmit={handleAddTodo}>
          <input
            type="text"
            placeholder="What needs to be done?"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            disabled={isLoading}
          />
          <button type="submit" disabled={!inputValue.trim()}>
            Add Todo
          </button>
        </form>
      </header>

      <main>
        <div className="filters">
          <button
            className={currentFilter === 'all' ? 'active' : ''}
            onClick={() => handleFilterChange('all')}
          >
            All ({activeTodosCount + completedTodosCount})
          </button>
          <button
            className={currentFilter === 'active' ? 'active' : ''}
            onClick={() => handleFilterChange('active')}
          >
            Active ({activeTodosCount})
          </button>
          <button
            className={currentFilter === 'completed' ? 'active' : ''}
            onClick={() => handleFilterChange('completed')}
          >
            Completed ({completedTodosCount})
          </button>
        </div>

        <ul className="todo-list">
          {filteredTodos.map((todo) => (
            <li key={todo.id} className={todo.completed ? 'completed' : ''}>
              <input
                type="checkbox"
                checked={todo.completed}
                onChange={() => toggleTodo(todo.id)}
              />
              <span className="todo-text">{todo.text}</span>
              <button 
                onClick={() => deleteTodo(todo.id)}
                className="delete-button"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>

        {filteredTodos.length === 0 && (
          <div className="empty-state">
            {currentFilter === 'all' 
              ? 'No todos yet. Add one above!'
              : `No ${currentFilter} todos.`
            }
          </div>
        )}

        {hasCompletedTodos && (
          <button 
            onClick={clearCompleted}
            className="clear-completed"
          >
            Clear Completed ({completedTodosCount})
          </button>
        )}
      </main>

      <footer>
        <p>{activeTodosCount} item{activeTodosCount !== 1 ? 's' : ''} left</p>
      </footer>
    </div>
  );
};

export default TodoApp;

