import { ReactNode } from 'react';
import { Todo, TodoFilter, TodoAction, TodoState } from './index';

/**
 * Props for TodoItem component
 */
export interface TodoItemProps {
  todo: Todo;
  onToggle: (id: string) => void;
  onUpdate: (id: string, text: string) => void;
  onDelete: (id: string) => void;
  isEditing?: boolean;
}

/**
 * Props for TodoList component
 */
export interface TodoListProps {
  todos: Todo[];
  onToggleTodo: (id: string) => void;
  onUpdateTodo: (id: string, text: string) => void;
  onDeleteTodo: (id: string) => void;
  emptyMessage?: string;
}

/**
 * Props for TodoFilter component
 */
export interface TodoFilterProps {
  currentFilter: TodoFilter;
  onFilterChange: (filter: TodoFilter) => void;
  todoCounts?: {
    all: number;
    active: number;
    completed: number;
  };
}

/**
 * Props for AddTodo component
 */
export interface AddTodoProps {
  onAddTodo: (text: string) => void;
  placeholder?: string;
  maxLength?: number;
}

/**
 * Props for TodoApp container
 */
export interface TodoAppProps {
  initialTodos?: Todo[];
  persistToLocalStorage?: boolean;
  className?: string;
  children?: ReactNode;
}

/**
 * Hook return type for useTodos custom hook
 */
export interface UseTodosReturn {
  state: TodoState;
  dispatch: (action: TodoAction) => void;
  actions: {
    addTodo: (text: string) => void;
    updateTodo: (id: string, text: string) => void;
    deleteTodo: (id: string) => void;
    toggleTodo: (id: string) => void;
    clearCompleted: () => void;
    setFilter: (filter: TodoFilter) => void;
  };
  selectors: {
    filteredTodos: Todo[];
