'use client';

import { TodoStatus } from '@/types';

interface FilterProps {
  currentFilter: TodoStatus;
  onFilterChange: (filter: TodoStatus) => void;
  todoCounts: {
    all: number;
    active: number;
    completed: number;
  };
}

export function Filter({ currentFilter, onFilterChange, todoCounts }: FilterProps) {
  const filters: { key: TodoStatus; label: string; count: number }[] = [
    { key: 'all', label: 'All', count: todoCounts.all },
    { key: 'active', label: 'Active', count: todoCounts.active },
    { key: 'completed', label: 'Completed', count: todoCounts.completed },
  ];

  return (
    <div className="flex justify-center mb-6">
      <div className="inline-flex rounded-lg border border-gray-200 bg-gray-50 p-1">
        {filters.map(({ key, label, count }) => (
          <button
            key={key}
            onClick={() => onFilterChange(key)}
            className={`px-4 py-2 text-sm font-medium rounded-md transition-colors ${
              currentFilter === key
                ? 'bg-white text-blue-600 shadow-sm'
                : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
            }`}
          >
            {label}
            <span className="ml-2 text-xs bg-gray-200 text-gray-600 px-2 py-1 rounded-full">
              {count}
            </span>
          </button>
        ))}
      </div>
    </div>
  );
}

