import { useReducer, useEffect, useMemo, useCallback } from 'react';
import { Todo, CreateTodoInput, UpdateTodoInput } from '../../../types/todo';
import { TodoState, initialTodoState, TodoSelectors } from '../../../types/state';
import { TodoAction, TodoActionType } from '../../../types/actions';
import { TodoFilter } from '../../../types/filters';

const STORAGE_KEY = 'todos';

/**
 * Utility functions for localStorage persistence
 */
const storageUtils = {
  loadTodos: (): Todo[] => {
    try {
      const stored = localStorage.getItem(STORAGE_KEY);
      if (!stored) return [];

      const parsed = JSON.parse(stored);
      return parsed.map((todo: any) => ({
        ...todo,
        createdAt: new Date(todo.createdAt),
        updatedAt: new Date(todo.updatedAt)
      }));
    } catch (error) {
      console.error('Failed to load todos from localStorage:', error);
      return [];
    }
  },

  saveTodos: (todos: Todo[]): void => {
    try {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(todos));
    } catch (error) {
      console.error('Failed to save todos to localStorage:', error);
    }
  }
};

/**
 * Todo reducer function handling all CRUD operations
 */
function todoReducer(state: TodoState, action: TodoAction): TodoState {
  switch (action.type) {
    case TodoActionType.LOAD_TODOS:
      return {
        ...state,
        todos: action.payload.todos,
        isLoading: false,
        error: null
      };

    case TodoActionType.ADD_TODO: {
      try {
        const newTodo: Todo = {
          id: crypto.randomUUID(),
          text: action.payload.text.trim(),
          completed: false,
          createdAt: new Date(),
          updatedAt: new Date()
        };

        if (!newTodo.text) {
          return {
            ...state,
            error: 'Todo text cannot be empty'
          };
        }

        const updatedTodos = [...state.todos, newTodo];
        storageUtils.saveTodos(updatedTodos);

        return {
          ...state,
          todos: updatedTodos,
          error: null
        };
      } catch (error) {
        return {
          ...state,
          error: 'Failed to add todo'
        };
      }
    }

    case TodoActionType.UPDATE_TODO: {
      try {
        const { id, updates } = action.payload;
        const updatedTodos = state.todos.map(todo =>
          todo.id === id
            ? { ...todo, ...updates, updatedAt: new Date() }
            : todo
        );

        storageUtils.saveTodos(updatedTodos);

        return {
          ...state,
          todos: updatedTodos,
          error: null
        };
      } catch (error) {
        return {
          ...state,
          error: 'Failed to update todo'
        };
      }
    }

    case TodoActionType.DELETE_TODO: {
      try {
        const updatedTodos = state.todos.filter(todo => todo.id !== action.payload.id);
        storageUtils.saveTodos(updatedTodos);

        return {
          ...state,
          todos: updatedTodos,
          error: null
        };
      } catch (error) {
        return {
          ...state,
          error: 'Failed to delete todo'
        };
      }
    }

    case TodoActionType.TOGGLE_TODO: {
      try {
        const updatedTodos = state.todos.map(todo =>
          todo.id === action.payload.id
            ? { ...todo, completed: !todo.completed, updatedAt: new Date() }
            : todo
        );

        storageUtils.saveTodos(updatedTodos);

        return {
          ...state,
          todos: updatedTodos,
          error: null
        };
      } catch (error) {
        return {
          ...state,
          error: 'Failed to toggle todo'
        };
      }
    }

    case TodoActionType.CLEAR_COMPLETED: {
      try {
        const updatedTodos = state.todos.filter(todo => !todo.completed);
        storageUtils.saveTodos(updatedTodos);

        return {
          ...state,
          todos: updatedTodos,
          error: null
        };
      } catch (error) {
        return {
          ...state,
          error: 'Failed to clear completed todos'
        };
      }
    }

    case TodoActionType.SET_FILTER:
      return {
        ...state,
        currentFilter: action.payload.filter,
        error: null
      };

    default:
      return state;
  }
}

/**
 * Custom hook return type
 */
export interface UseTodosReturn extends TodoSelectors {
  todos: Todo[];
  currentFilter: TodoFilter;
  isLoading: boolean;
  error: string | null;
  addTodo: (input: CreateTodoInput) => void;
  updateTodo: (id: string, updates: UpdateTodoInput) => void;
  deleteTodo: (id: string) => void;
  toggleTodo: (id: string) => void;
  clearCompleted: () => void;
  setFilter: (filter: TodoFilter) => void;
  clearError: () => void;
}

/**
 * Custom hook for managing todo state with CRUD operations and localStorage persistence
 */
export const useTodos = (): UseTodosReturn => {
  const [state, dispatch] = useReducer(todoReducer, {
    ...initialTodoState,
    isLoading: true
  });

  // Load todos from localStorage on mount
  useEffect(() => {
    const loadTodos = async () => {
      try {
        const savedTodos = storageUtils.loadTodos();
        dispatch({
          type: TodoActionType.LOAD_TODOS,
          payload: { todos: savedTodos }
        });
      } catch (error) {
        dispatch({
          type: TodoActionType.LOAD_TODOS,
          payload: { todos: [] }
        });
      }
    };

    loadTodos();
  }, []);

  // Computed selectors
  const selectors = useMemo((): TodoSelectors => {
    const filteredTodos = state.todos.filter(todo => {
      switch (state.currentFilter) {
        case 'active':
          return !todo.completed;
        case 'completed':
          return todo.completed;
        default:
          return true;
      }
    });

    const activeTodosCount = state.todos.filter(todo => !todo.completed).length;
    const completedTodosCount = state.todos.filter(todo => todo.completed).length;

    return {
      filteredTodos,
      activeTodosCount,
      completedTodosCount,
      allTodosCount: state.todos.length,
      hasCompletedTodos: completedTodosCount > 0
    };
  }, [state.todos, state.currentFilter]);

  // Action creators
  const addTodo = useCallback((input: CreateTodoInput) => {
    dispatch({
      type: TodoActionType.ADD_TODO,
      payload: input
    });
  }, []);

  const updateTodo = useCallback((id: string, updates: UpdateTodoInput) => {
    dispatch({
      type: TodoActionType.UPDATE_TODO,
      payload: { id, updates }
    });
  }, []);

  const deleteTodo = useCallback((id: string) => {
    dispatch({
      type: TodoActionType.DELETE_TODO,
      payload: { id }
    });
  }, []);

  const toggleTodo = useCallback((id: string) => {
    dispatch({
      type: TodoActionType.TOGGLE_TODO,
      payload: { id }
    });
  }, []);

  const clearCompleted = useCallback(() => {
    dispatch({
      type: TodoActionType.CLEAR_COMPLETED
    });
  }, []);

  const setFilter = useCallback((filter: TodoFilter) => {
    dispatch({
      type: TodoActionType.SET_FILTER,
      payload: { filter }
    });
  }, []);

  const clearError = useCallback(() => {
    dispatch({
      type: TodoActionType.SET_FILTER,
      payload: { filter: state.currentFilter }
    });
  }, [state.currentFilter]);

  return {
    // State
    todos: state.todos,
    currentFilter: state.currentFilter,
    isLoading: state.isLoading,
    error: state.error,

    // Selectors
    ...selectors,

    // Actions
    addTodo,
    updateTodo,
    deleteTodo,
    toggleTodo,
    clearCompleted,
    setFilter,
    clearError
  };
};

